sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
	var whatneeded = g_form.getValue('u_what_needed_2');

	g_form.clearOptions('u_what_needed_2');
	if(newValue == 'human resources'){
		g_form.addOption('u_what_needed_2' , 'human resources1' , 'Human resources 1');
		g_form.addOption('u_what_needed_2' , 'human resources2' , 'Human resources 2');
		g_form.addOption('u_what_needed_2' , 'others' , 'others');
	}
	if(newValue == 'legal'){
		g_form.addOption('u_what_needed_2' , 'legal1' , 'Legal 1');
		g_form.addOption('u_what_needed_2' , 'legal2' , 'Legal 2');
		g_form.addOption('u_what_needed_2' , 'others' , 'others');
	}
if(newValue == 'facilities'){
	g_form.addOption('u_what_needed_2' , 'facilities1' , 'Facility 1');
	g_form.addOption('u_what_needed_2' , 'facilities2' , 'Facility 2');
	g_form.addOption('u_what_needed_2' , 'others' , 'others');

	//if(isLoading && !g_form.isNewRecord()){
	//	g_form.setValue('u_what_needed_2' , 'u_what_needed_2');
	//}
}
   //Type appropriate comment here, and begin script below

}
